declare const FormGroupStoryCode = "import React from 'react';\nimport {\n  Box,\n  Heading,\n  TextInput,\n  PasswordInput,\n  Button,\n  ArrowRightIcon,\n  Alert,\n} from '@razorpay/blade/components';\n\nconst FormExample = () => {\n  const [formData, setFormData] = React.useState({\n    email: '',\n    password: '',\n  });\n\n  const [errors, setErrors] = React.useState<Record<string, string>>({});\n  const [alert, setAlert] = React.useState<{\n    type: 'positive' | 'negative' | null;\n    title: string;\n    description: string;\n  } | null>(null);\n\n  const handleChange = (name: string, value: string) => {\n    setFormData((prev) => ({ ...prev, [name]: value }));\n    setErrors((prev) => ({ ...prev, [name]: '' }));\n  };\n\n  const validateForm = () => {\n    const newErrors: Record<string, string> = {};\n\n    if (!formData.email) {\n      newErrors.email = 'Email is required';\n    } else if (!/\\S+@\\S+\\.\\S+/.test(formData.email)) {\n      newErrors.email = 'Invalid email format';\n    }\n\n    if (!formData.password) {\n      newErrors.password = 'Password is required';\n    } else if (formData.password.length < 8) {\n      newErrors.password = 'Password must be at least 8 characters';\n    }\n\n    setErrors(newErrors);\n    return Object.keys(newErrors).length === 0;\n  };\n\n  const handleSubmit = (e: React.FormEvent) => {\n    e.preventDefault();\n    if (validateForm()) {\n      setAlert({\n        type: 'positive',\n        title: 'Success!',\n        description: 'Your form has been submitted successfully.',\n      });\n    } else {\n      setAlert({\n        type: 'negative',\n        title: 'Form Submission Failed',\n        description: 'Please fix the errors in the form and try again.',\n      });\n    }\n  };\n\n  return (\n    <form onSubmit={handleSubmit}>\n      <Box padding=\"spacing.4\" display=\"flex\" flexDirection=\"column\" gap=\"spacing.4\">\n        <Box>\n          <Heading size=\"large\">Welcome to Blade Example</Heading>\n          <Heading size=\"medium\" weight=\"regular\">\n            This is an example form built with Blade\n          </Heading>\n        </Box>\n\n        {alert && (\n          <Alert\n            color={alert.type}\n            title={alert.title}\n            description={alert.description}\n            emphasis=\"subtle\"\n            isDismissible\n            onDismiss={() => setAlert(null)}\n          />\n        )}\n\n        <Box display=\"flex\" flexDirection=\"column\" gap=\"spacing.4\">\n          <TextInput\n            isRequired\n            label=\"Email\"\n            name=\"email\"\n            value={formData.email}\n            onChange={({ value }) => handleChange('email', value)}\n            validationState={errors.email ? 'error' : 'none'}\n            errorText={errors.email}\n          />\n          <PasswordInput\n            isRequired\n            label=\"Password\"\n            name=\"password\"\n            value={formData.password}\n            onChange={({ value }) => handleChange('password', value)}\n            helpText=\"Should be more than 8 characters\"\n            validationState={errors.password ? 'error' : 'none'}\n            errorText={errors.password}\n          />\n          <Button isFullWidth type=\"submit\" icon={ArrowRightIcon} iconPosition=\"right\">\n            Sign In\n          </Button>\n        </Box>\n      </Box>\n    </form>\n  );\n};\n\nexport default FormExample;\n\n";
export { FormGroupStoryCode };
